// Generated by Selenium IDE
const { Builder, By, Key, until } = require('selenium-webdriver')
const assert = require('assert')

describe('harvest_onion_crop', function() {
  this.timeout(30000)
  let driver
  let vars
  beforeEach(async function() {
    driver = await new Builder().forBrowser('chrome').build()
    vars = {}
  })
  afterEach(async function() {
    await driver.quit();
  })
  it('harvest_onion_crop', async function() {
    // Test name: harvest_onion_crop
    // Step # | name | target | value
    // 1 | open | http://127.0.0.1:5501/bahay_cube.html | 
    await driver.get("http://127.0.0.1:5501/bahay_cube.html")
    // 2 | setWindowSize | 1294x1392 | 
    await driver.manage().window().setRect({ width: 1294, height: 1392 })
    // 3 | click | id=tile_id_5 | 
    await driver.findElement(By.id("tile_id_5")).click()
    // 4 | click | css=.till_btn | 
    await driver.findElement(By.css(".till_btn")).click()
    // 5 | click | id=tile_id_5 | 
    await driver.findElement(By.id("tile_id_5")).click()
    // 6 | verifyElementPresent | css=.tilled | 
    {
      const elements = await driver.findElements(By.css(".tilled"))
      assert(elements.length)
    }
    // 7 | click | css=.plant_btn | 
    await driver.findElement(By.css(".plant_btn")).click()
    // 8 | click | xpath=//label[@id='onion_crop'] | 
    await driver.findElement(By.xpath("//label[@id=\'onion_crop\']")).click()
    // 9 | click | id=crop_to_plant_btn | 
    await driver.findElement(By.id("crop_to_plant_btn")).click()
    // 10 | verifyElementPresent | css=.onion_planted | 
    {
      const elements = await driver.findElements(By.css(".onion_planted"))
      assert(elements.length)
    }
    // 11 | verifyText | css=.total_earnings_value | 85
    assert(await driver.findElement(By.css(".total_earnings_value")).getText() == "85")
    // 12 | waitForElementPresent | css=.harvest | 8000
    await driver.wait(until.elementLocated(By.css(".harvest")), 8000)
    // 13 | waitForText | css=#tile_id_5 > .tile_text | $25
    await driver.wait(until.elementTextIs(await driver.findElement(By.css("#tile_id_5 > .tile_text")), '$25'), 30000)
    // 14 | click | id=tile_id_5 | 
    await driver.findElement(By.id("tile_id_5")).click()
    // 15 | click | id=harvest_btn | 
    await driver.findElement(By.id("harvest_btn")).click()
    // 16 | verifyText | css=.total_earnings_value | 110
    assert(await driver.findElement(By.css(".total_earnings_value")).getText() == "110")
  })
})
